class Js_visible_element{
    visibleY= elm=>{
        let top=elm.getBoundingClientRect().top;
        let bottom=elm.getBoundingClientRect().bottom;
            if(top<0 && bottom<0){return false;}
            if(window.innerHeight<=top){return false}return true;
        }
    visibleX= elm=>{
        let left=elm.getBoundingClientRect().left;
        let right=elm.getBoundingClientRect().right;
            if(left<0 && right<0){return false;}return true;
        }
    isVisible= elm=>{return new Promise( (res,rej)=>{
        let element={visible:true,element:elm}
            if(this.visibleX(elm) && this.visibleY(elm)){res(element)}else{element.visible=false;rej(element)}})
        }
}